// query string
msg.payload =
[
    { $project: {
        node: true, 
        h: true, 
        t: true, 
        l: true, 
        timestamp: true, 
        _id: true,
        hour: { $hour: "$timestamp"},
        month: { $month: "$timestamp"},
        year: { $year: "$timestamp"}
    }},
    { $match: { 
        node: msg.payload.node,
        month: parseInt(msg.payload.month), 
        year: parseInt(msg.payload.year) 
        } 
    },
    { $group: {
            _id: {
               hour:{$hour:"$timestamp"},
               day:{$dayOfMonth: "$timestamp"},
                month:{$month:"$timestamp"},
                year:{$year:"$timestamp"}
            },
            averageTemp: { $avg: "$t" },
            averageHumidity: { $avg: "$h"},
            averageLuminance: { $avg: "$l"},
            count: { $sum: 1 }
        }
    },
    {
        $sort: {
            "_id.year": 1, 
            "_id.month": 1, 
            "_id.day": 1, 
            "_id.hour":1 

        }
    }
];

return msg;
